use strict;
use warnings;

open(my $fh, '<', $file) or die $!;
my $sum;
while (my $line = <$fh>) {
  $sum = $sum + $line;
}
print "Sum: $sum\n";

use strict;
use warnings;

my $input = <STDIN>;
chomp $input;
if ($input =~ /^[\w.+-]+@[\w.-]+\.[A-Za-z]{2,}$/) {
  print "valid\n";
} else {
  print "invalid\n";
}
